query:
-- Atribuir centro de custos baseado no departamento
-- Formato: CC-{departmentId}-{nome_departamento}

UPDATE employees e
INNER JOIN departments d ON e.departmentId = d.id
SET e.costCenter = CONCAT('CC-', LPAD(d.id, 3, '0'), '-', UPPER(SUBSTRING(d.name, 1, 10)));

